sys_variable_value.value:
(function(outputs, steps, stepResult, assertEqual)
{
	var test = new x_snc_devtools.TestAPI(outputs, steps, stepResult, assertEqual);
	
	test.SetResult(true);

	var strCheck = 'b2ccee3b1b40b950cd6153d16b4bcb7b';
	
	var testdata = new x_snc_devtools.TestDataAPI('_ATF_CODESANITY_');

	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test1',
		'function _ATF_CODESANITY_Test1()\n'
		+'{ var strtest = "1234567890abcdef"; }');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,false);
	
	// Do not object sys_ids in comments
	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test2',
		'function _ATF_CODESANITY_Test1()\n'
		+'{ var strtest = "hello"; // sys_id in a comment: 12345678901234567890123456789012\n'
		+'}');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,false);	
	
	// Detect the sys_id if it is inside a string literal
	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test10',
		'function _ATF_CODESANITY_Test10()\n'
		+'{ var strtest = "12345678901234567890123456789012"; }');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,true);

	// Handle simple obfuscation strategies
	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test11',
		'function _ATF_CODESANITY_Test11()\n'
		+'{ var strtest = "1234567890123456"+"7890123456789012"; }');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,true);	
	
	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test12',
		'function _ATF_CODESANITY_Test12()\n'
		+'{ var strtest = \'1234567890123456\'+\'7890123456789012\'; }');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,true);		

	var strRecordSysId = testdata.CreateScriptInclude(
		'global',
		'Test12',
		'function _ATF_CODESANITY_Test12()\n'
		+'{ var strtest = \'1234567890123456\'   +  \'7890123456789012\'; }');
	test.TestCase_InstanceScanCheck(strCheck,'sys_script_include',strRecordSysId,true);		
	
	
	return test.GetResult();
	
})(outputs, steps, stepResult, assertEqual);
