sys_script_include.script:
function LinterCheck_IsCodeCompliant(grCheckRecord,engine)
{
	if (IsActive() == false)
	{
		return true;
	}

	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var GetStringValue = x_snc_devtools.GetStringValue;

	if (IsValidRecord(grCheckRecord) == false)
	{
		return true;
	}

	var strRule = GetStringValue(grCheckRecord.getValue('description'));
	var strCode = GetStringValue(engine.rootNode.toSource());

	return IsCodeCompliant(strRule,strCode,'JavaScript running on ServiceNow Server');
}
