sys_script_include.script:
function LinterCheck_IsCodeCompliant(engine,strRule)
{
	if (IsActive() == false)
	{
		return;
	}

	var IsValidRecord = x_snc_devtools.IsValidRecord;
	var GetStringValue = x_snc_devtools.GetStringValue;
	var GetCallStack = x_snc_devtools.GetCallStack;
	var GetValue = x_snc_devtools.GetValue;
	var GetRecord = x_snc_devtools.GetRecord;

	/*
	var aStack = GetCallStack();
	var grCheck = GetRecord(GetValue(aStack,1,'sys_class_name'),GetValue(aStack,1,'sys_id'));
	if (IsValidRecord(grCheck) == false)
	{
		return true;
	}
	*/


	/*
	var grRecord = null;
	if (typeof current != 'undefined')
	{
		grRecord = current;
	}
	if (IsValidRecord(grRecord) == false)
	{
		grRecord = x_snc_devtools.RunScriptInScope(grCheck.sys_scope,'result = current;');
	}
	if (IsValidRecord(grRecord) == false)
	{
		return true;
	}
	*/


	strRule = GetStringValue(strRule);
	var strCode = GetStringValue(engine.rootNode.toSource());

	var bCompliant = IsCodeCompliant(strRule,strCode,'JavaScript running on a ServiceNow Server');
	if (bCompliant != true)
	{
		engine.finding.increment();
	}

}
